import React from 'react';
import NoteCard from '../components/NoteCard';

class AllNotesFilter extends React.Component {

  state = {
    selectBeach: null,
  }
  componentDidMount = () => {

  }

  handleChange = (event) => {
    console.log("event", event.target)
    this.setState({
      selectBeach: event.target.value
    });
    console.log("handleChange", this.state)
  }

  handleSubmit = (event) => {
    // alert('Beach selected: ' + this.state.value);
    event.preventDefault();
    this.setState({
      selectBeach: event.target.value
    }, () => {console.log("after submit", this.state)});
  }

  sortedList = () => {
    return this.props.allBeaches.map(beach => <option key={beach.id} value={beach.name}>{beach.name}</option>);
  }
  // <option key={index}>{location}</option>


  render () {


    return (
      <div className="AllNotesFilter-Container">

        <form onSubmit={this.handleSubmit}>
        <h3>Filter by...</h3>
        <label>Beach</label>
          <select value={this.state.selectBeach} onChange={this.handleChange} >
           {this.sortedList()}
          </select>
        <input type="submit" value="Submit" />
        </form>

      </div>

    )
  }
}

export default AllNotesFilter;
