class Login extends React.Component {

  state = {
    username: "",
    password: ""
  }

  handleInput = (event) => {
    event.persist()
    this.setState({
      [event.target.name]: event.target.value
    })
  }

  handleSubmit = (event) => {
    event.preventDefault()
    //callback function here.
    this.props.loginUser(this.state)
  }

  render(){
    console.log("login", this.props)
